  const [activeFilter, setActiveFilter] = useState('all');
  const [activeReview, setActiveReview] = useState(0);
  const gridRef = useRef(null);

  const scrollLeft = () => {
    if (gridRef.current) {
      gridRef.current.scrollBy({ left: -300, behavior: 'smooth' });
